import React, { useState } from 'react'
//useState serve para fazer o gerenciamento do state
//use state retorna um array com duas posições 1- estado inicial(string) 2 - thispath com uma função
import './AdicionarUsuario.css'

const INITIAL_STATE = { 
  usuario: { nome: '', sobrenome: '', email: '' } 
}

//utilizando hooks

function AdicionarUsuario(props){

  const [nome, setNome] = useState('')
  const [sobrenome, setSobrenomeNome] = useState('')

  const onChangeHandler = (event) => {
    const { name, value } = event.target
    this.setState({ usuario: { ...this.state.usuario, [name]: value } })
  }

  const onSubmitHandler = (event) => {
    event.preventDefault()

    const usuario = this.state.usuario

    fetch('https://reqres.in/api/users', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(usuario)
    })
      .then(resposta => resposta.json())
      .then(dados => {
        this.setState(INITIAL_STATE)
        this.props.adicionarUsuario(dados)
      })
  }
  return (
    <div className="AdicionarUsuario">
      <h2>Adicionar Usuário</h2>
      <form onSubmit={this.onSubmitHandler}>
        <div className="Linha">
          <div className="Coluna">
            <label>Nome</label>
            <input
              type="text"
              name="nome"
              value={nome}
              onChange={event => setNome(event.target.value)}
              required>
            </input>
          </div>
          <div className="Coluna">
            <label>Sobrenome</label>
            <input
              type="text"
              name="sobrenome"
              value={sobrenome}
              onChange={event => setSobreNome(event.target.value)}
              required>
            </input>
          </div>
        </div>
        <div className="Linha">
          <div className="Coluna">
            <label>Email</label>
            <input
              type="email"
              name="email"
              value={this.state.usuario.email}
              onChange={this.onChangeHandler}
              required>
            </input>
          </div>
        </div>
        <button type="submit">
          Adicionar
      </button>
      </form>
    </div>
  )
}

